mapper.select:
SELECT /* org.mt.mms.estimate.mapper.EstimateMapper.selectEstimates : ESTIMATE 목록조회 */ E.ESTIMATE_ID , E.TOP_CONTR_ID , E.COMP_ID , E.ORDER_NO , E.ESTIMATE_DIV , E.ESTIMATE_FILE_ID , (SELECT CC.CODE_NM FROM CMM_CODE CC WHERE CC.CODE = E.ESTIMATE_DIV AND CC.CODE_GROUP = 'ESTD') AS ESTIMATE_DIV_NM , E.CONFIRM_YN , E.CONTR_YN , DATE_FORMAT(E.REGIST_DATE, '%Y-%m-%d') AS REGIST_DATE , E.REGIST_USER_NAME
, TC.TOP_CONTR_NM , C.COMP_NM , CN.CONTR_ID FROM ESTIMATE E INNER JOIN TOP_CONTR TC ON TC.TOP_CONTR_ID = E.TOP_CONTR_ID INNER JOIN COMP C ON C.COMP_ID = E.COMP_ID LEFT OUTER JOIN CONTR CN ON CN.ESTIMATE_ID = E.ESTIMATE_ID WHERE E.DEL_YN = 'N'
AND TC.TOP_CONTR_ID = #{topContrId}
AND C.COMP_ID = #{compId}
AND E.ESTIMATE_DIV = #{estimateDiv}
ORDER BY E.REGIST_DATE DESC

mapper.insert:
INSERT INTO ESTIMATE /* org.mt.mms.estimate.mapper.EstimateMapper.insertEstimate : ESTIMATE 등록 */
(
 ESTIMATE_ID
, TOP_CONTR_ID
, COMP_ID
, ORDER_NO
, ESTIMATE_DIV
, CONFIRM_YN
, CONTR_YN
, ESTIMATE_FILE_ID
, REGIST_USER_NAME
)
VALUES
(
  #{estimateId}
, #{topContrId}
, #{compId}
, (SELECT IFNULL(MAX(ORDER_NO), 0)+1
   FROM ESTIMATE ALIAS_FOR_SUBQUERY
   WHERE TOP_CONTR_ID=#{topContrId} AND COMP_ID=#{compId} AND ESTIMATE_DIV = #{estimateDiv}
)
, #{estimateDiv}
, 'N'
, 'N'
, #{estimateFileId}
, #{registUserName}
)

mapper.select:
SELECT COUNT(*) /* org.mt.mms.estimate.mapper.EstimateMapper.selectPossibleConfirm : ESTIMATE 확정여부 조회 */
FROM ESTIMATE
WHERE DEL_YN = 'N'
  AND TOP_CONTR_ID = #{topContrId}
  AND COMP_ID = #{compId}
  AND ESTIMATE_DIV = #{estimateDiv}
  AND CONFIRM_YN = 'Y'

mapper.update:
UPDATE ESTIMATE SET /* org.mt.mms.estimate.mapper.EstimateMapper.updateConfirmEstimate : ESTIMATE 확정 */
    CONFIRM_YN = 'Y'
WHERE ESTIMATE_ID = #{id}

UPDATE ESTIMATE SET /* org.mt.mms.estimate.mapper.EstimateMapper.deleteEstimate : ESTIMATE 삭제 */
    DEL_YN = 'Y'
WHERE ESTIMATE_ID = #{id}

UPDATE ESTIMATE SET /* org.mt.mms.estimate.mapper.EstimateMapper.updateContrEstimate */
    CONTR_YN = 'Y'
WHERE ESTIMATE_ID = #{id}
